import React, { Component } from 'react';
import "./Home.css";
import Carousel from 'react-bootstrap/Carousel'


// Animation

class Home extends Component {
  render() {
    return (
  
      <Carousel>
        <Carousel.Item>
          <img
            className="d-block w-100"
            src="images/hero1.jpg"
            alt="First slide"
          />
          <Carousel.Caption>
          
          <h2 className="mt-3"> We connect farmers looking for tractors with tractor owners looking for work</h2>
          </Carousel.Caption>
        </Carousel.Item>

        <Carousel.Item>
          <img
            className="d-block w-100"
            src="images/hero4.jpg"
            alt="First slide"
          />
          <Carousel.Caption>
          
          <h2 className="mt-3"> We connect farmers looking for tractors with tractor owners looking for work</h2>
          </Carousel.Caption>
        </Carousel.Item>
        <Carousel.Item>
          <img
            className="d-block w-100"
            src="images/hero2.jpg"
            alt="Third slide"
          />
      
          <Carousel.Caption>
          <h2 className="mt-3"> We connect farmers looking for tractors with tractor owners looking for work</h2>
          </Carousel.Caption>
        </Carousel.Item>
        
        <Carousel.Item>
          <img
            className="d-block w-100"
            src="images/hero3.jpg"
            alt="Third slide"
          />
      
          <Carousel.Caption>
          <h2 className="mt-3 disp"> We connect farmers looking for tractors with tractor owners looking for work</h2>
          </Carousel.Caption>
        </Carousel.Item>
      </Carousel>

    // <section className="hero">
    //   <div className="container-fluid">
